# TH XSTK Bai 3: Bien ngau nhien (Tuan 3-4)# Vi du 1 a# Tao vec to k cรณ gia tri tu 0 den 8k = 0:8# Viet ham xac suatp <- function(k) choose(8,k) * 0.3^k * 0.7^(8-k)# Tinh gia tri xac suat P(X = k), k = 0,1,...,8p(k)# Kiem tra tong cac xac suat bang 1sum(p(k))# Vi du 1 b# Viet ham mat do cua bien ngau nhien X ~ N(mu = 0,sigma^2 = 1)f <- function(x, mu=0, sigma=1){  1/sqrt(2*pi*sigma^2) * exp(-(x-mu)^2/(2*sigma^2))}# Kiem tra tich phan bang 1integrate(function(x) f(x,0,1),lower=-Inf,upper=Inf)# Tinh f(0)f(0)# Vi du 2 a# Ve ham xac suat o vi du 1aplot(k, p(k), type = "h", xlab = "k", ylab = "P(X = k)", main = "Ham xac suat cua bnn X")# Vi du 2 b# Ve ham mat do xac suat o vi du 1bcurve(f(x,0,1),from=-3,to=3, xlab = "x", ylab = "fX(x)", main = "Ham mat do cua bnn X")